 {				var selected = this.element.children( ":selected" ),					value = selected.val() ? selected.text() : "";				this.input = $( "<input>" )					.appendTo( this.wrapper )					.val( value )					.attr( "title", "" )					.addClass( "custom-combobox-input ui-widget ui-widget-content ui-state-default ui-corner-left" )					.autocomplete({						delay: 0,						minLength: 0,						source: $.proxy( this, "_source" )					})					.tooltip({						classes: {							"ui-tooltip": "ui-state-highlight"						}					});				this._on( this.input, {					autocompleteselect: function( event, ui ) {						ui.item.option.selected = true;						this._trigger( "select", event, {							item: ui.item.option						});					},					autocompletechange: "_removeIfInvalid"				});			},			_createShowAllButton: function() {				var input = this.input,					wasOpen = false;				$( "<a>" )					.attr( "tabIndex", -1 )					.attr( "title", "Show All Items" )					.tooltip()					.appendTo( this.wrapper )					.button({						icons: {							primary: "ui-icon-triangle-1-s"						},						text: false					})					.removeClass( "ui-corner-all" )					.addClass( "custom-combobox-toggle ui-corner-right" )					.on( "mousedown", function() {						wasOpen = input.autocomplete( "widget" ).is( ":visible" );					})					.on( "click", function() {						input.trigger( "focus" );						// Close if already visible						if ( wasOpen ) {							return;						}						// Pass empty string as value to search for, displaying all results						input.autocomplete( "search", "" );					});			},			_source: function( request, response ) {				var matcher = new RegExp( $.ui.autocomplete.escapeRegex(request.term), "i" );				response( this.element.children( "option" ).map(function() {					var text = $( this ).text();					if ( this.value && ( !request.term || matcher.test(text) ) )						return {							label: text,							value: text,							option: this						};				}) );			},			_removeIfInvalid: function( event, ui ) {				// Selected an item, nothing to do				if ( ui.item ) {					return;				}				// Search for a match (case-insensitive)				var value = this.input.val(),					valueLowerCase = value.toLowerCase(),					valid = false;				this.element.children( "option" ).each(function() {					if ( $( this ).text().toLowerCase() === valueLowerCase ) {						this.selected = valid = true;						return false;					}				});				// Found a match, nothing to do				if ( valid ) {					return;				}				// Remove invalid value				this.input					.val( "" )					.attr( "title", value + " didn't match any item" )					.tooltip( "open" );				this.element.val( "" );				this._delay(function() {					this.input.tooltip( "close" ).attr( "title", "" );				}, 2500 );				this.input.autocomplete( "instance" ).term = "";			},			_destroy: function() {				this.wrapper.remove();				this.element.show();			}		});		$( "#combobox" ).combobox();		$( "#toggle" ).on( "click", function() {			$( "#combobox" ).toggle();		});	} );	</script><link href="https://fonts.googleapis.com/css?family=Hind+Siliguri" rel="stylesheet"><script src="https://cdn.datatables.net/1.10.19/js/jquery.dataTables.min.js"></script><script src="https://cdn.datatables.net/1.10.19/js/dataTables.bootstrap4.min.js"></script>	</head><body>	<!DOCTYPE html>	<html>	<head>		<meta name="viewport" content="width=device-width, initial-scale=1">		<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css">		<style>			/* Fixed sidenav, full height */.sidenav {    height: 100%;    width: 200px;    position: fixed;    z-index: 1;    top: 50px;    left: 0;    background-color: #2c3e50;    overflow-x: hidden;    font-family: Ubuntu-R;}/* Style the sidenav links and the dropdown button */.sidenav a, .dropdown-btn {    padding: 6px 8px 6px 20px;    text-decoration: none;    color: #f2f2f2;